function month_to_num(month){
  if (month === "Jan"){
    num = '01'
  }
  else if (month === "Feb"){
    num = '02'
  }
  else if (month === "Mar"){
    num = '03'
  }
  else if (month === "Apr"){
    num = '04'
  }
  else if (month === "May"){
    num = '05'
  }
  else if (month === "Jun"){
    num = '06'
  }
  else if (month === "Jul"){
    num = '07'
  }
  else if (month === "Aug"){
    num = '08'
  }
  else if (month === "Sep" ){
    num = '09'
  }
  else if (month === "Oct") {
    num = '10'
  }
  else if (month === "Nov" ){
    num = '11'
  }
  else if (month ==="Dec" ){
    num = '12'
  }
  return num;
}

function date_to_week(date){
  parts = date.split(' ')
  month = parseInt(month_to_num(parts[1]))
  day = parseInt(parts[2])
  if (month === 1){
    week = ~~(day / 7)
    week = week + 1
  } else{
    a = (month -1 )*30 + day
    a = ~~(a / 7)
    week = a +1
  }
  return week;
}



